      <Card>
        <Card.Content>
          <Image floated='right' src='https://www.planetnatural.com/wp-content/uploads/2012/12/sage-herb.jpg' />
          <Card.Header>Sage</Card.Header>
          <Card.Meta>Herb</Card.Meta>
          <Card.Description>
            Sage is a herb
          </Card.Description>
        </Card.Content>
        <Card.Content>
            <Card.Description>
                Moisture: {this.state.sensor}
            </Card.Description>
        </Card.Content>
