          <Card.Header>Thyme</Card.Header>
          <Card.Meta>Herb</Card.Meta>
          <Card.Description>Thyme is a herb</Card.Description>
        </Card.Content>
        <Card.Content extra>
          <div className='ui two buttons'>
            <Button basic color='green'>
              Add
            </Button>
            <Button basic color='red'>
            Remove
          </Button>
        </div>
      </Card.Content>
    </Card>
  </Card.Group>
            </>
        );
    }
}




