import React from 'react';
import jsonData from '../data/plants.json';
import {connect} from 'react-redux';
import {addToCollectionDb, displayCollectionDb, removeFromCollectionDb} from '../actions/';
import {
    Button,
    // Container,
    // Divider,
    // Grid,
    // Header,
    // Icon,
    Image,
    Label,
    // List,
    // Menu,
    // Responsive,
